import React, { useState, useEffect } from 'react';
import { StyleSheet, Image, View, Text } from 'react-native';
import { ListItem } from 'react-native-elements';
import { API } from '../Config/Api';

function Detail(props) {
    const [books, setBooks] = useState([]);
    const [loading, setLoading] = useState(true);
    const id = props.route.params.id;

    useEffect(() => {
        book();
    }, []);

    const book = async () => {
        try {
          const books = await API.get(`/books/${id}`);
          await setBooks(books.data.data.book);
          setLoading(false);
        } catch (error) {
          console.log(error);
        } 
    }
    return (
        <View style={styles.container}>
            {loading? (
                <Text>Loading ...</Text>
            ):(
                <ListItem style={styles.listItem}>
                    <ListItem.Content>
                        <Image source={require('../Images/CoverBook/'+books.coverFile)} style={styles.coverBook} />
                        <ListItem.Title h5 style={styles.title}>{books.title}</ListItem.Title>
                        <ListItem.Subtitle>{books.author}</ListItem.Subtitle>
                        <ListItem.Title>"{books.about}"</ListItem.Title>
                    </ListItem.Content>
                </ListItem>
            )}
        </View>
    )
}

const styles = StyleSheet.create({
    container:{
        width: "100%",
        height: "100%",
        backgroundColor: "#E5E5E5",
        alignItems: "center",
        // justifyContent: "center",
        padding: 10,
    },
    title: {
        fontWeight: 500
    },
    listItem: {
        width: "95%",
    },
    coverBook: {
        width: 200, 
        height: 270,
        alignContent: "center"
    },
});

export default Detail
