import React, { useState, useEffect } from 'react';
import { StyleSheet, Image, View } from 'react-native';
import { ListItem, Avatar } from 'react-native-elements';
import { API } from '../Config/Api';

function HomeScreen() {
    const [books, setBooks] = useState([]);

    useEffect(() => {
        book();
    }, []);

    const book = async () => {
        try {
          const books = await API.get("/books");
          await setBooks(books.data.data.books);
        } catch (error) {
          console.log(error);
        } 
    }
    return (
        <View style={styles.container}>
            {books.map((book) => (
            <ListItem style={styles.listItem} bottomDivider>
                <Image source="assets/icon.png" style={styles.logo} />
                {/* <Avatar rounded title="H" containerStyle={{backgroundColor: 'black'}} /> */}
                <ListItem.Content>
                    <ListItem.Title h6 numberOfLines={2}>{book.title}</ListItem.Title>
                    <ListItem.Subtitle numberOfLines={3}>{book.about}</ListItem.Subtitle>
                </ListItem.Content>
                <ListItem.Chevron />
            </ListItem>
            ))}
        </View>
    )
}

const styles = StyleSheet.create({
    container:{
        width: "100%",
        height: "100%",
        backgroundColor: "#E5E5E5",
        alignItems: "center",
        // justifyContent: "center",
        padding: 10,
    },
    listItem: {
        width: "95%",
    },
    logo: {
        marginTop: 10,
        width: 126, 
        height: 80,

    },
});

export default HomeScreen
